import React, { useState } from 'react';
const Username = (props) => {
  const value = props;
  const { username, setUser } = useState(value);

  return (
    <h6>
    <input type="text" placeholder="Username" value={username} onChange={(event) => { setUser(event.target.username) }} />
    </h6>
  )
}
export default Username;
