import React, { useState } from 'react';
const Password = (props) => {
  const value = props;
  const [password, setPass ] = useState(value);

  return (
    <h6>
    <input type="text" placeholder="Password" value={password} onChange={(event) => { setPass(event.target.password) }} />
    </h6>
  )
}
export default Password;
