import React from 'react';
export default class State extends React.Component{

    constructor(){
        super();
        this.state={
            name:'Chattishgarh'
        }
    }
    updateName(){

        this.setState({
            name:"Kolkata"
        });
    }
    render(){

        return(
            <div>
                <h6>Welcome to {this.state.name}</h6>
                <button onClick={()=>this.updateName()}>Update </button>
            </div>
        )
    }

}
