import React, { useState } from 'react';
import * as yup from 'yup';
import {  InputGroup } from 'reactstrap';

let value = '';
let errortext='';
const Username = () => {
  let schema = yup.object().shape({
    usernameEntered: yup.string().required()
  })


  const [username, setUsername] = useState(value);
  const [errortext, showError] = useState(false);
  const checkValid = (input) => {
    schema.isValid({
      usernameEntered: input


    }).then(function (valid) {
      console.log(valid);
    });

    schema.validate({usernameEntered: input}).catch(function(err){

     console.log( err.name);
     errortext=err.errors;
      console.log(err.errors);
    })
  }


  return (
    <div>
      <InputGroup>
        <h6>
          <input type="text" placeholder="Username" onChange={(event) => {
            setUsername(event.target.value)
            checkValid(username)
          }} value={username}  />
          <p onChange={(event)=>{
            showError(event.target.errortext)
          }}> {errortext}  </p>

        </h6>
        <br />

      </InputGroup>
    </div>

  )
}
export default Username;
